import { useState, useEffect } from "react";
import { Card, CardImg, CardBody, CardTitle, CardText } from "reactstrap";


function SearchPhoto({ onSearch }) {
  const [query, setQuery] = useState("");
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:3000/Photos?q=${query}`)
      .then((response) => response.json())
      .then((data) => setPhotos(data))
      .catch((error) => console.error(error));
  }, [query]);

  function handleSubmit(event) {
    event.preventDefault();
    onSearch(query);
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <p>Search for your favourite photo</p>
        <input
          type="text"
          value={query}
          onChange={(event) => setQuery(event.target.value)}
          placeholder="Search here"
        />
        <button type="submit">Search</button>
      </form>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {photos.map((photo) => (
          <Card key={photo.id} style={{ width: "300px", margin: "10px" }}>
            <CardImg top width="100%" src={photo.cover} alt={photo.title} />
            <CardBody>
              <CardTitle>{photo.title}</CardTitle>
              <CardText>Price: ${photo.price}</CardText>
            </CardBody>
          </Card>
        ))}
      </div>
    </div>
  );
}

export default SearchPhoto;
