import React, { useState } from "react";
import { Container, Row, Col, Card, CardBody, CardTitle, Form, FormGroup, Label, Input, Button } from "reactstrap";

function UpdateButton({ onAdd }) {
  const [id, setId] = useState("");
  const [Title, setTitle] = useState("");
  const [Price, setPrice] = useState("");
  const [Cover, setCover] = useState("");

  function handleSubmit(event) {
    event.preventDefault();
    const newStyle = { id, Title, Price, Cover };
    fetch(`http://localhost:3000/Photos/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newStyle),
    })
      .then((response) => response.json())
      .then((data) => onAdd(data))
      .catch((error) => console.error(error));

    onAdd(newStyle);
    setId("");
    setTitle("");
    setPrice("");
    setCover("");
  }

  return (
    <Container>
      <Row>
        <Col>
          <Card id="update">
            <CardBody>
              <CardTitle tag="h3">
                <u>UPDATE A PHOTO HERE:</u>
              </CardTitle>
              <Form onSubmit={handleSubmit}>
                <FormGroup>
                  <Label for="id">Photo ID:</Label>
                  <Input type="text" value={id} onChange={(e) => setId(e.target.value)} id="id" />
                </FormGroup>
                <FormGroup>
                  <Label for="title">Title:</Label>
                  <Input type="text" value={Title} onChange={(e) => setTitle(e.target.value)} id="title" />
                </FormGroup>
                <FormGroup>
                  <Label for="price">Price:</Label>
                  <Input type="text" value={Price} onChange={(e) => setPrice(e.target.value)} id="price" />
                </FormGroup>
                <FormGroup>
                  <Label for="cover">Cover:</Label>
                  <Input type="text" value={Cover} onChange={(e) => setCover(e.target.value)} id="cover" />
                </FormGroup>
                <Button id="button10" type="submit">
                  Update Photo
                </Button>
              </Form>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default UpdateButton;
